<script>
    let urlCurrent = "{{ url()->full() }}";
    urlCurrent = unEntity(urlCurrent);

    function page(page_number) {
        urlCurrent = updateQueryStringParameter(urlCurrent, "page", page_number);
        urlCurrent = unEntity(urlCurrent);
        urlCurrent = decodeURI(urlCurrent);
        window.location = urlCurrent;
    }

    function nextPage(page) {
        page = getPageFromUrl(page);
        urlCurrent = updateQueryStringParameter(urlCurrent, "page", page);
        urlCurrent = unEntity(urlCurrent);
        urlCurrent = decodeURI(urlCurrent);
        window.location = urlCurrent;
    }

    function getPageFromUrl(url) {
        return new URL(url).searchParams.get('page');
    }

    //Update end set param url
    function updateQueryStringParameter(uri, key, value) {
        var re = new RegExp("([?&])" + key + "=.*?(&|$)", "i");
        var separator = uri.indexOf('?') !== -1 ? "&" : "?";
        if (uri.match(re)) {
            return uri.replace(re, '$1' + key + "=" + value + '$2');
        } else {
            return uri + separator + key + "=" + value;
        }
    }

    //End Update end set param url

    //Replace &amp url
    function unEntity(str) {
        return str.replace(/&amp;/g, "&").replace(/&lt;/g, "<").replace(/&gt;/g, ">");
    }

    //End replace
</script>
